import {
   Banner, Card, DisplayText, Subheading,
   Form, FormLayout, Frame, Layout, TextStyle,
   Page, PageActions, TextField, Heading
} from '@shopify/polaris';
import store from 'store-js';
import gql from 'graphql-tag';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';
import { Mutation } from 'react-apollo';


class EditProduct extends React.Component {
   state = {
      businessName: '',
      streetAddress: '',
      city: '',
      zipCode: '',
      state: '',
      merchantDescription: ''
   };

   static contextType = Context;

   render() {
      const app = this.context;
      const { name, price, discount, variantId } = this.state;
      const item = store.get('product');
      const redirectToHome = () => {
         const redirect = Redirect.create(app);
         redirect.dispatch(
            Redirect.Action.APP,
            '/index',
         )
      }
      return (
         <Frame>
            <Page>
               <Layout>
                  <Layout.Section>
                     <DisplayText size="large">{name}</DisplayText>
                     <Form>
                        <Card sectioned>
                           <Heading>Voucher Display Form: {item ? item.title : ''}</Heading>
                           <TextStyle variation="subdued">Fill in the data below for the merchant corresponding to this product.</TextStyle> <br /><br />
                           <FormLayout>
                              <Subheading>About</Subheading>
                              <TextField autoFocus={true} prefix="" value={this.state.businessName} onChange={this.handleChange('businessName')}
                                 label="Merchant's Name â€“ As displayed in Google Maps" type="text" placeholder="Bob's Burger Shack" maxLength={150} />
                              <TextField prefix="" value={this.state.merchantDescription} onChange={this.handleChange('merchantDescription')}
                                 label="Merchant Description" type="text" maxLength={800} showCharacterCount={true} spellCheck={true} multiline={true}
                                 placeholder="Bob's Burger Shack is a gourmet dine-in restaurant located in the Financial District of Downtown Seattle. They're known for..." />
                              <Subheading>Address</Subheading>
                              <FormLayout.Group condensed>
                                 <TextField maxLength={75} prefix="" value={this.state.streetAddress} onChange={this.handleChange('streetAddress')}
                                    label="Street Address" type="text" placeholder="48185 Washington Blvd." />
                                 <TextField maxLength={50} prefix="" value={this.state.city} onChange={this.handleChange('city')}
                                    label="City" type="text" label="City" placeholder="Seattle" />
                                 <TextField maxLength={5} prefix="" label="Zip Code" value={this.state.zipCode} onChange={this.handleChange('zipCode')}
                                    placeholder="90210" type="number" />
                                 <TextField maxLength={2} prefix="" label="State" value={this.state.state} onChange={this.handleChange('state')}
                                    placeholder="WA" />
                              </FormLayout.Group>
                           </FormLayout>
                        </Card>
                        <PageActions
                           primaryAction={[{
                              content: 'Save', onAction: () => {
                                 this.handleSubmit(item);
                              }
                           }]}
                           secondaryActions={[{
                              content: 'Return To List',
                              onAction: () => {
                                 redirectToHome();
                              }
                           }]} />
                     </Form>
                  </Layout.Section>
               </Layout>
            </Page>
         </Frame>
      );
   }

   handleChange = (field) => {
      return (value) => this.setState({ [field]: value });
   }
   handleSubmit = (item) => {
      console.log("**",item)
      let updatedItem = {...item, ...this.state};
      console.log("^^",updatedItem)
   }
}

export default EditProduct;

