import { Page, Layout, TextStyle, EmptyState } from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import store from 'store-js';
import ResourceListWithProducts from '../components/ResourceList';

const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';

class Index extends React.Component {
    state = { open: false };
    render() {
        const emptyState = !store.get('ids');
        return (
            <Page>
                <TitleBar
                    primaryAction={{
                        content: 'Select products',
                        onAction: () => this.fetchProducts()
                        // onAction: () => this.setState({ open: true })
                    }} />
                <ResourcePicker
                    resourceType="Product"
                    showVariants={false}
                    open={this.state.open}
                    onSelection={(resources) => this.handleSelection(resources)}
                    onCancel={() => this.setState({ open: false })}
                />
                {emptyState ? (
                    <Layout>
                        {/* <TextStyle variation="positive">Let's get this BREAD AAASSSAAAPPP</TextStyle> */}
                        <EmptyState
                            heading="Send Vouchers to your customers to be redeemed in-store."
                            action={{
                                content: 'Select Products',
                                onAction: () => this.setState({ open: true })
                            }}
                            image={img}>
                            <p>Select products for which to send vouchers to customers via email.</p>
                        </EmptyState>
                    </Layout>
                ) : (
                        <ResourceListWithProducts />
                    )}
            </Page>
        );
    }
    handleSelection = (resources) => {
        const idsFromResources = resources.selection.map((product) => product.id)
        this.setState({ open: false })
        console.log(idsFromResources)
        store.set('ids', idsFromResources)
    }
    fetchProducts = () => {
        console.log("IN Here fetchProducts()")
        fetch('/api/products')
        .then(res => console.log(res))
        .catch(err => console.log(err))
    }
}

export default Index;
