import {
   Banner, Card, DisplayText, Subheading,
   Form, FormLayout, Frame, Layout, TextStyle,
   Page, PageActions, TextField, Heading
} from '@shopify/polaris';
import store from 'store-js';
import gql from 'graphql-tag';
import { Mutation } from 'react-apollo';

class EditProduct extends React.Component {
   state = {
      businessName: '',
      streetAddress: '',
      city: '',
      zipCode: '',
      state: '',
      merchantDescription: ''
   };

   componentDidMount() {
      this.setState({ discount: this.itemToBeConsumed() });
   }

   render() {
      const { name, price, discount, variantId } = this.state;
      const item = store.get('product');
      console.log(item)
      return (
         <Frame>
            <Page>
               <Layout>
                  <Layout.Section>
                     <DisplayText size="large">{name}</DisplayText>
                     <Form>
                        <Card sectioned>
                           <Heading>Voucher Display Form: {item.title}</Heading>
                           <TextStyle variation="subdued">Fill in the data below for the corresponding merchant.</TextStyle> <br /><br />
                           <FormLayout>
                              {/* <FormLayout.Group> */}
                              <TextField prefix="" value={this.state.businessName} disabled={false} onChange={this.handleChange('businessName')}
                                 label="Merchant's Name" type="text" placeholder="Bob's Burger Shack" />
                              <Subheading>Address</Subheading>
                              <FormLayout.Group condensed>
                                 <TextField prefix="" value={this.state.streetAddress} onChange={this.handleChange('streetAddress')}
                                    label="Street Address" type="text" placeholder="48185 Washington Blvd." />
                                 <TextField prefix="" value={this.state.city} onChange={this.handleChange('city')}
                                    label="City" type="text" label="City" placeholder="Seattle" />
                                 <TextField prefix="" label="Zip Code" value={this.state.zipCode} onChange={this.handleChange('zipCode')}
                                    placeholder="90210" />
                                 <TextField prefix="" label="State" value={this.state.state} onChange={this.handleChange('state')}
                                    placeholder="WA" />
                              </FormLayout.Group>
                              {/* </FormLayout.Group> */}
                              <p> This sale price will expire in two weeks </p>
                           </FormLayout>
                        </Card>
                        <PageActions
                           primaryAction={[{
                              content: 'Save', onAction: () => {
                                 const productVariableInput = {
                                    id: variantId,
                                    price: discount,
                                 };
                                 handleSubmit({
                                    variables: { input: productVariableInput }
                                 });
                              }
                           }]}
                           secondaryActions={[{ content: 'Remove discount' }]} />
                     </Form>
                  </Layout.Section>
               </Layout>
            </Page>
         </Frame>
      );
   }

   handleChange = (field) => {
      return (value) => this.setState({ [field]: value });
   }

   itemToBeConsumed = () => {
      // const item = store.get('item');
      // const price = item.variants.edges[0].node.price;
      // const variantId = item.variants.edges[0].node.id;
      // const discounter = price * 0.1;
      // this.setState({ price, variantId });
      // return (price - discounter).toFixed(2);
   }
}

export default EditProduct;

const UPDATE_PRICE = gql`
  mutation productVariantUpdate($input: ProductVariantInput!) {
    productVariantUpdate(input: $input) {
      product {
        title
      }
      productVariant {
        id
        price
      }
    }
  }
`;
