import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { Card, ResourceList, Stack, TextStyle, Thumbnail } from '@shopify/polaris';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';

class ResourceListWithProducts extends React.Component {

    static contextType = Context;

    render() {
        const app = this.context;
        const redirectToProduct = () => {
            const redirect = Redirect.create(app);
            redirect.dispatch(
                Redirect.Action.APP,
                '/edit-products',
            )
        }
        const twoWeeksFromNow = new Date(Date.now() + 12096e5).toDateString();
        // return (<div><TextStyle>INSIDE RESOUCE LST</TextStyle></div>)
        return (
            <Card>
                <ResourceList
                    showHeader
                    resourceName={{ singular: 'Product', plural: 'Products' }}
                    items={this.props.products}
                    renderItem={(item, id, index) => {
                        const media = (
                            <Thumbnail
                                source={item.images[0] ? item.images[0].originalSrc : ''}
                                alt={item.images[0] ? item.images[0].altText : ''} />
                        );
                        const price = 30;
                        return (
                            <ResourceList.Item id={item.id} media={media}
                                accessibilityLabel={`View details for ${item.title}`}
                                onClick={() => {
                                    store.set('item', item);
                                    redirectToProduct();
                                }} >
                                <Stack>
                                    <Stack.Item fill>
                                        <h3> <TextStyle variation="strong"> {item.title} </TextStyle> </h3>
                                    </Stack.Item>
                                    <Stack.Item> <p>${price}</p> </Stack.Item>
                                    <Stack.Item> <p>Expires on {twoWeeksFromNow} </p> </Stack.Item>
                                </Stack>
                            </ResourceList.Item>
                        );
                    }}
                />
            </Card>
        );
    }
}

export default ResourceListWithProducts;
