import { Page, Layout, TextStyle, EmptyState, Spinner } from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
// import store from 'store-js';
import axios from 'axios';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import ResourceListWithProducts from '../components/ResourceList';

const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';

class Index extends React.Component {
    state = { open: false, loadingDB: true, products: null, productIDs: null };

    async componentDidMount() {
        let products = await this.fetchProducts();
        console.log("PRODUCTS: ", products)
        if (products.data == "Delicious panther") //IF not products , show 
            //Set products in state
            this.setState({ loadingDB: false })
    }

    componentWillUnmount() {
        console.log("UNMOUNTINGIGNIG")
    }
    render() {
        const emptyState = this.state.products === null;
        const { loadingDB, productIDs } = this.state;
        if (loadingDB) { //Fetching Products from Database 
            return (<Page>
                <Layout>
                    <EmptyState centeredLayout heading={'Fetching Products from Database...'}>
                        <Spinner accessibilityLabel="Spinner" size="large" color="inkLightest" />
                    </EmptyState>
                </Layout>
            </Page>
            )
        }
        if (productIDs) {
            return ( //If exists, user gave Voucher functionality to these products
                <Page>
                    <Layout>
                        <EmptyState centeredLayout heading={'Adding Specified Products to Database...'}>
                            <Query query={GET_PRODUCTS_BY_ID} variables={{ ids: productIDs }}>
                                {({ data, loading, error }) => {
                                    if (loading) return <Spinner accessibilityLabel="Spinner" size="large" color="inkLightest" />;
                                    if (error) return <div>{error.message}</div>
                                    else {
                                        console.log("GOT DATA", data)
                                        this.postNewProductsToDB(data);
                                        return <Spinner accessibilityLabel="Spinner" size="large" color="inkLightest" />;
                                    }
                                }}
                            </Query>
                        </EmptyState>
                    </Layout>
                </Page>
            )
        }
        return (
            <Page>
                <TitleBar
                    primaryAction={{
                        content: 'Select services',
                        onAction: () => this.setState({ open: true })
                    }} />
                <ResourcePicker
                    resourceType="Product"
                    showVariants={false}
                    open={this.state.open}
                    onSelection={(resources) => this.handleSelection(resources)}
                    onCancel={() => this.setState({ open: false })}
                />
                {emptyState ? (
                    <Layout>
                        {/* <TextStyle variation="positive">Let's get this BREAD AAASSSAAAPPP</TextStyle> */}
                        <EmptyState
                            heading="Send Vouchers to your customers to be redeemed in-store."
                            action={{
                                content: 'Select Services',
                                onAction: () => this.setState({ open: true })
                            }}
                            image={img} >
                            <p>Select products/services for which to send vouchers to customers via email.</p>
                        </EmptyState>
                    </Layout>
                ) : (
                        <ResourceListWithProducts />
                    )}
            </Page>
        );
    }

    postNewProductsToDB = async (products) => {
        console.log("******", products, "********")
        // await fetch('/api/products', {
        //     method: 'POST',
        //     body: products.nodes,
        //     headers: {
        //         "Content-Type": "application/json"
        //     }
        // })
        console.log("IN HRERERE")
        await axios.post('/api/products', { products: products.nodes }, {
            headers: { "Content-Type": "application/json" }
        }).then(res => console.log(res))
            .catch(err => console.log(err))
        let fetchedProducts = await this.fetchProducts()
        this.setState({ products: fetchedProducts, productIDs: null })
    }
    handleSelection = async (resources) => {
        const idsFromResources = resources.selection.map((product) => product.id)
        this.setState({ open: false })
        // console.log(idsFromResources, "^&^%%^%^%^%^")
        // await getProducts(idsFromResources)
        this.setState({ productIDs: idsFromResources })
    }
    fetchProducts = async () => {
        console.log("IN Here fetchProducts()")
        return await fetch('/api/products')
            .then(res => res.json())
            .then(response => {
                return response
            })
            .catch(err => {
                alert(err)
                return null
            })
    }
}

export default Index;

const GET_PRODUCTS_BY_ID = gql`
  query getProducts($ids: [ID!]!) {
    nodes(ids: $ids) {
      ... on Product {
        title
        handle
        descriptionHtml
        id
        images(first: 10) {
          edges {
            node {
              originalSrc
              altText
            }
          }
        }
      }
    }
  }
`;
