import { Card, ResourceList, Stack, TextStyle, Thumbnail, Button, Form } from '@shopify/polaris';
import store from 'store-js';
import { Redirect } from '@shopify/app-bridge/actions';
import { Context } from '@shopify/app-bridge-react';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';

class ResourceListWithProducts extends React.Component {
    state = {
        updating: {},
        loading: {}
    }
    static contextType = Context;
    handleUpload = (id, index) => {
        console.log(id, index)
        let updating = this.state.updating;
        let loading = this.state.loading;
        updating[id] = true
        loading[index] = true
        this.setState({ updating: updating, loading: loading })
    }
    render() {
        const app = this.context;
        const redirectToProduct = () => {
            const redirect = Redirect.create(app);
            redirect.dispatch(
                Redirect.Action.APP,
                '/edit-products',
            )
        }
        console.log(this.state)
        return (
            <Card>
                <ResourceList showHeader resourceName={{ singular: 'Product', plural: 'Products' }}
                    items={this.props.products}
                    renderItem={(item, _, indx) => {
                        let complete = false;
                        if (item.streetAddress && item.city && item.email && item.zipCode
                            && item.state && item.businessName && item.merchantDescription) {
                            complete = true
                        }
                        const media = (
                            <Thumbnail
                                source={item.images[0] ? item.images[0].originalSrc : ''}
                                alt={item.images[0] ? item.images[0].altText : ''} />
                        );
                        return (
                            <ResourceList.Item id={item.id} media={media}
                                accessibilityLabel={`View details for ${item.title}`} >
                                <Stack>
                                    <Stack.Item fill>
                                        <h3> <TextStyle variation="strong"> {item.title} </TextStyle> </h3>
                                    </Stack.Item>
                                    <Stack.Item>
                                        {this.state.updating[item.id] ?
                                            <Button loading={this.state.loading[indx]} primary>Update Item</Button>
                                            :
                                            <Form loading={this.state.loading[indx]} onSubmit={() => this.handleUpload(item.id, indx)} preventDefault={true}>
                                                <Button submit primary>Update Item</Button>
                                            </Form>
                                        }
                                    &nbsp; 12/30 &nbsp;
                                    <div style={{ color: complete ? "green" : "red" }}>
                                            <Button monochrome outline onClick={() => { store.set('product', item); redirectToProduct(); }}>{complete ? "Complete" : "Incomplete"} </Button>
                                        </div>
                                    </Stack.Item>
                                </Stack>
                            </ResourceList.Item>
                        );
                    }}
                />
            </Card>
        );
    }
}

export default ResourceListWithProducts;

const GET_PRODUCTS_BY_ID = gql`
  query getProducts($ids: [ID!]!) {
    nodes(ids: $ids) {
      ... on Product {
        title
        descriptionHtml
        id
        variants(first:4){
          edges{
            node{
              compareAtPrice
              price
              title
            }
          }
        }
        images(first: 10) {
          edges {
            node {
              originalSrc
              altText
            }
          }
        }
      }
    }
  }
`;
