export default (req, res) => {
    const { shopOrigin } = req.cookies;
    const { method } = req;
    console.log(shopOrigin)
    res.setHeader('Content-Type', 'application/json')
    res.statusCode = 200;
    switch (method) {
        case 'GET': 
            console.log("FETCH DB")
            res.status(200).json({d: "ata from DB"})
            break;
        case 'POST':
            console.log("POST DATA")
            res.status(200).json({p: "ost data to DB"})
            break;
        default:
            res.setHeader('Allow', ['GET', 'POST'])
            res.status(405).end(`Method ${method} Not Allowed.`)
    }
}
