const dotenv = require('dotenv');
const AWS = require('aws-sdk');
dotenv.config();
AWS.config.update({
    region: 'us-west-2', credentials: {
        accessKeyId: process.env.accessKeyId,
        secretAccessKey: process.env.secretAccessKey
    }
});
var docClient = new AWS.DynamoDB.DocumentClient();

export default async (req, res) => {
    const { shopOrigin } = req.cookies;
    res.setHeader('Content-Type', 'application/json')

    switch (req.method) {
        case 'GET':
            let fetchedProducts = await fetchProductsFromDB(shopOrigin)
            res.status(200).json({ data: fetchedProducts });
            break;
        case 'POST':
            let postProducts = req.body.products.map(prod => {
                delete prod.__typename;
                delete prod.handle;
                let images = prod.images.edges.map(img => {
                    delete img.__typename;
                    delete img.node.__typename;
                    return img;
                })
                prod.images = images;
                prod.shopOrigin = shopOrigin;
                return prod
            })
            let success = await postProductstoDB(postProducts)
            res.status(200).json({ success });
            break;
        default:
            res.setHeader('Allow', ['GET', 'POST'])
            res.status(405).end(`Method ${method} Not Allowed.`)
            break;
    }
}

async function fetchProductsFromDB(shopOrigin, count = 0) {
    const params = {
        TableName: 'Shopify_Products',
        FilterExpression: "shopOrigin = :shop",
        ExpressionAttributeValues: { ':shop': shopOrigin}
    }
    docClient.scan(params, async (err, products) => {
        if(err) {
            console.log(err)
            if (count == 30) return null;
            await fetchProductsFromDB(shopOrigin, ++count)
        } else {
            console.log("GETTING PRODUCTS))()()()(()()()(",products)
            return products.Items;
        }
    })
}

async function postProductstoDB(products, count = 0) {
    if (products.length == 0) return true;
    const params = {
        TableName: 'Shopify_Products',
        Item: { ...products[products.length - 1] }
    };
    docClient.put(params, async (err, data) => {
        if (err) {
            console.log(err)
            if (count == 30) return false;
            await postProductstoDB(products, ++count)
        } else {
            if (products.length > 0) {
                products.pop();
                await postProductstoDB(products, count)
            }
            return true
        }
    })
}
