import { Page, Layout, TextStyle, EmptyState, Spinner } from '@shopify/polaris';
import { ResourcePicker, TitleBar } from '@shopify/app-bridge-react';
import store from 'store-js';
import gql from 'graphql-tag';
import { useQuery } from '@apollo/react-hooks';
import ResourceListWithProducts from '../components/ResourceList';

const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';

class Index extends React.Component {
    state = { open: false, loading: true, products: null };

    async componentDidMount() {
        console.log("Did MOUNT")
        let products = await this.fetchProducts();
        console.log("PRODUCTS: ", products)
        if (products.data == "Delicious panther") //IF not products , show 
            //Set products in state
            this.setState({ loading: false })
    }

    componentWillUnmount() {
        console.log("UNMOUNTINGIGNIG")
    }
    render() {
        const emptyState = this.state.products;
        const { loading } = this.state;
        if (loading) return (
            <Page>
                <Layout>
                    <EmptyState centeredLayout heading={'Fetching Products from Database...'}>
                        <Spinner accessibilityLabel="Spinner" size="large" color="inkLightest" />
                    </EmptyState>
                </Layout>
            </Page>
        )
        return (
            <Page>
                <TitleBar
                    primaryAction={{
                        content: 'Select services',
                        onAction: () => this.setState({ open: true })
                    }} />
                <ResourcePicker
                    resourceType="Product"
                    showVariants={false}
                    open={this.state.open}
                    onSelection={(resources) => this.handleSelection(resources)}
                    onCancel={() => this.setState({ open: false })}
                />
                {emptyState ? (
                    <Layout>
                        {/* <TextStyle variation="positive">Let's get this BREAD AAASSSAAAPPP</TextStyle> */}
                        <EmptyState
                            heading="Send Vouchers to your customers to be redeemed in-store."
                            action={{
                                content: 'Select Services',
                                onAction: () => this.setState({ open: true })
                            }}
                            image={img}>
                            <p>Select products/services for which to send vouchers to customers via email.</p>
                        </EmptyState>
                    </Layout>
                ) : (
                        <ResourceListWithProducts />
                    )}
            </Page>
        );
    }
    handleSelection = async (resources) => {
        const idsFromResources = resources.selection.map((product) => product)
        this.setState({ open: false })
        console.log(idsFromResources)
        // await getProducts(idsFromResources)
        store.set('ids', idsFromResources)
    }
    fetchProducts = async () => {
        console.log("IN Here fetchProducts()")
        return await fetch('/api/products')
            .then(res => res.json())
            .then(response => {
                return response
            })
            .catch(err => {
                alert(err)
                return null
            })
    }
}

export default Index;

async function getProducts(idsFromResources) {
    const { loading, error, data } = useQuery(GET_PRODUCTS_BY_ID, { variables: { ids: idsFromResources } })
    console.log(loading, error, data, "HELOO BOOIOIOIO we out here")
}

const GET_PRODUCTS_BY_ID = gql`
  query getProducts($ids: [ID!]!) {
    nodes(ids: $ids) {
      ... on Product {
        title
        handle
        descriptionHtml
        id
        images(first: 10) {
          edges {
            node {
              originalSrc
              altText
            }
          }
        }
        variants(first: 1) {
          edges {
            node {
              price
              id
            }
          }
        }
      }
    }
  }
`;
