export default (req, res) => {
    const { shopOrigin } = req.cookies;
    const { method } = req;
    console.log(shopOrigin, method)
    res.setHeader('Content-Type', 'application/json')

    switch (method) {
        case 'GET': 
            console.log("FETCH DB")
            // res.status(200).json({d: "ata from DB"})
            res.send({ data: "Delicious panther" });
            break;
        case 'POST':
            console.log("POST DATA\n___________________")
            let products = req.body.products.forEach(prod => {
                delete prod.__typename;
                delete prod.handle;
                let images = prod.images.edges.forEach(img => {
                    console.log(img)
                    return img;
                })
                console.log(images)
                prod.images = images;
                // console.log(prod)
                return prod
            })
            res.status(200).json({p: req.body})
            break;
        default:
            res.setHeader('Allow', ['GET', 'POST'])
            res.status(405).end(`Method ${method} Not Allowed.`)
    }
}
