const dotenv = require('dotenv');
const AWS = require('aws-sdk');
const sgMail = require('@sendgrid/mail');
const short = require('short-uuid');
// const NodeGeocoder = require('node-geocoder');
// const geocoder = NodeGeocoder({
//     apiKey: 'AIzaSyAfTmvVvLL6-SdQy6O5GcgiG3vxoDDEhic',
//     formatter: null
// })
dotenv.config();
sgMail.setApiKey(process.env.sendGridKey);
AWS.config.update({
    region: 'us-west-2', credentials: {
        accessKeyId: process.env.accessKeyId,
        secretAccessKey: process.env.secretAccessKey
    }
});
var docClient = new AWS.DynamoDB.DocumentClient();


module.exports = {
    // getCoords: async (street, city, zipCode, state) => {
    //     let res =  await geocoder.geocode(`${street} ${city}, ${state} ${zipCode}`)
    //     console.log(res)
    //     return "HELLLO"
    // },
    sendVoucherLink: async (shop, email, title, variant_title, id) => {
        const p_params = {
            TableName: 'Shopify_Products',
            FilterExpression: 'shopOrigin = :shop and title = :t',
            ExpressionAttributeValues: { ':shop': shop, ':t': title }
        }
        let awsRequest = await docClient.scan(p_params);
        let result = await awsRequest.promise();
        if (result.Items.length > 0) {
            console.log("Received Item")
            const transaction_id = short.generate();
            const v_code = Date.now().toString();
            let variantPurchased = null;
            for (let variant of result.Items[0].variants) {
                if (variant.title == variant_title) {
                    variantPurchased = variant
                    variantPurchased.variant_title = variant_title;
                    variantPurchased.title = title
                }
            }
            //Check if transaction has already been made due to faulty webhook
            const check_params = {
                TableName: 'Deal_Transactions',
                FilterExpression: 'order_id = :id and variant_title =:v',
                ExpressionAttributeValues: { ':id': id, ':v': variant_title.length > 0 ? variant_title : null }
            }
            let awsTReq = await docClient.scan(check_params)
            let tResult = await awsTReq.promise()
            if (tResult.Items.length > 0) return
            //Get variant from variant array & add to transaction
            const t_params = {
                TableName: 'Deal_Transactions',
                Item: {
                    transaction_id, order_id: id, email, shopOrigin: shop,
                    ...variantPurchased, time_purchased: Date.now(),
                    v_code: v_code.substring(v_code.length - 5),
                    time_redeemed: null, rating: null, review: null,
                    status: 'PURCHASED', vendor: result.Items[0].id
                }
            }
            awsRequest = await docClient.put(t_params);
            let res = await awsRequest.promise();
            if (Object.keys(res).length > 0) return false
            console.log("Created Transaction")
            let link = `https://scottsdeal.offerware.co/${transaction_id}/redeem/${result.Items[0].id}?shop=${shop}`
            // Eventually replace variant in email for more targerted content
            // if (!result.Items[0].email) return //Temporary while developing
            let sendEmail = result.Items[0].email.replace('{{Auto-Generated Link}}', link)
            console.log(sendEmail)
            const msg = {
                to: email, from: 'scottsdeal@offerware.co',
                subject: `Your Voucher Link for: ${variant_title}.`,
                text: sendEmail,
            }
            let resMail = await sgMail.send(msg)
            return true
        }
    },
    updateProductInDB: async (title, handle, shop, descriptionHtml, images, variants) => {
        console.log("UPDATING")
        const title_params = {
            TableName: 'Shopify_Products',
            FilterExpression: 'shopOrigin = :shop and title = :t',
            ExpressionAttributeValues: { ':shop': shop, ':t': title }
        }
        let awsRequest = docClient.scan(title_params);
        let result = await awsRequest.promise();
        let updatedProduct = null;
        // First check If title did not change & update db if found
        if (result.Items.length > 0) {
            updatedProduct = { ...result.Items[0], title, handle, descriptionHtml }
            await updateDB(updatedProduct, images, variants)
            return
        } else { // Check if handle did not change & update db if found
            const handle_params = {
                TableName: 'Shopify_Products',
                FilterExpression: 'shopOrigin = :shop and handle = :h',
                ExpressionAttributeValues: { ':shop': shop, ':h': handle }
            }
            awsRequest = docClient.scan(handle_params);
            result = await awsRequest.promise();
            if (result.Items.length > 0)
                updatedProduct = { ...result.Items[0], title, handle, descriptionHtml }
            await updateDB(updatedProduct, images, variants)
            // else do nothing because Shopify Admin did not subscribe
            // to Voucher capabilities with this products
            return
        }
    },
}

async function updateDB(updatedProduct, images, variants) {
    let newImages = [];
    for (let image of images)
        newImages.push({ 'altText': image.alt, 'originalSrc': image.src })
    updatedProduct.images = newImages;
    let newVariants = [];
    for (let variant of variants)
        newVariants.push({ 'title': variant.title, 'price': variant.price, 'compareAtPrice': variant.compare_at_price })
    updatedProduct.variants = newVariants
    let updated_params = {
        TableName: 'Shopify_Products',
        Item: updatedProduct
    }
    let awsReq = await docClient.put(updated_params);
    let res = awsReq.promise();
    return;
}
